import { Component } from '@angular/core';
import {AuthService} from "../../../auth/services/auth-service";
import {AccountPasswordService} from "./account-password-service";
import {ToastyService} from "ng2-toasty/index";
import {AppService} from "../../../services/app-service";

@Component({
    selector: 'account-password',
    styles: [

    ],
    template: `
        <div class="col-md-9 sec-main">
            <h3 class="line">
                Change Password
            </h3>

            <h4>Password Change Confirmation</h4>

            <div class="row">
                <div class="form-group col-md-5">
                    <label for="exampleInputPassword1">Current Password</label>
                    <input [(ngModel)]="oldPass" type="password" class="form-control c-theme" id="firstname" style="background-image: url(&quot;data:image/png;base64,&quot;); background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; background-repeat: no-repeat; cursor: auto;" autocomplete="off">
                </div>
            </div>

            <div class="row">
                <div class="form-group col-md-5">
                    <label for="exampleInputPassword1">New Password</label>
                    <input [(ngModel)]="newPass" type="password" class="form-control c-theme" id="firstname" style="background-image: url(&quot;data:image/png;base64,&quot;); background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; background-repeat: no-repeat;">
                </div>
            </div>

            <div class="row">
                <div class="form-group col-md-5">
                    <label for="exampleInputPassword1">Confirm New Password</label>
                    <input [(ngModel)]="newPassRepeat" type="password" class="form-control c-theme" id="firstname" style="background-image: url(&quot;data:image/png;base64,&quot;); background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; background-repeat: no-repeat;">
                </div>
            </div>

            <br>

            <a class="btn btn-primary btn-lg" (click)="updatePasswordInit()">Submit</a>

        </div>
    `
})

export class AccountPasswordComponent {

    oldPass:string;
    newPass:string;
    newPassRepeat:string;

    constructor(private auth: AuthService, private toasty: ToastyService, private appService: AppService){

    }

    updatePasswordInit(){
        this.updatePassword(this.newPass, this.oldPass);
    }

    updatePassword(newPass, oldPass){

        this.appService.startLoadingBar();

        this.auth.updatePassword(newPass, oldPass).subscribe((data)=>{

            if(data.status == "success"){

                let toast = {
                    title: "Success",
                    msg: "Your password has been updated!"
                };

                this.appService.completeLoadingBar();
                this.toasty.success(toast);

            }
            else{
                let toast = {
                    title: "Error",
                    msg: data.message
                };

                this.appService.completeLoadingBar();
                this.toasty.error(toast);

            }


        })
    }

}
